import React from 'react'

import { Field, Stack } from '@chakra-ui/react'
import { HStack } from '@chakra-ui/react'
import { Meta, type StoryObj } from '@storybook/react-vite'

import { Checkbox } from './checkbox.tsx'

export default {
  title: 'Components/Checkbox',
  component: Checkbox,
}

type Story = StoryObj<typeof Checkbox>

export const Default: Story = {}

export const Checked: Story = {
  args: {
    checked: true,
  },
}

export const Indeterminate: Story = {
  args: {
    checked: 'indeterminate',
  },
}

export const Disabled: Story = {
  args: {
    disabled: true,
  },
}

export const Sizes: Story = {
  render: () => (
    <HStack>
      <Stack>
        <Checkbox size="sm" />
        <Checkbox size="md" />
        <Checkbox size="lg" />
      </Stack>
      <Stack>
        <Checkbox size="sm" checked />
        <Checkbox size="md" checked />
        <Checkbox size="lg" checked />
      </Stack>
    </HStack>
  ),
}

export const Variants: Story = {
  render: () => (
    <HStack>
      <Stack>
        <Checkbox variant="outline" />
        <Checkbox variant="outline" checked />
      </Stack>
      <Stack>
        <Checkbox variant="solid" />
        <Checkbox variant="solid" checked />
      </Stack>
      <Stack>
        <Checkbox variant="subtle" />
        <Checkbox variant="subtle" checked />
      </Stack>
    </HStack>
  ),
}

export const WithLabel: Story = {
  render: () => <Checkbox>Accept terms and conditions</Checkbox>,
}

export const WithLabelDisabled: Story = {
  render: () => <Checkbox disabled>Accept terms and conditions</Checkbox>,
}

'use client'

import { forwardRef } from 'react'

import { Checkbox as ChakraCheckbox } from '@chakra-ui/react/checkbox'

export interface CheckboxProps extends ChakraCheckbox.RootProps {
  icon?: React.ReactNode
  inputProps?: React.InputHTMLAttributes<HTMLInputElement>
  rootRef?: React.Ref<HTMLLabelElement>
}

export const Checkbox = forwardRef<HTMLInputElement, CheckboxProps>(
  function Checkbox(props, ref) {
    const { icon, children, inputProps, rootRef, ...rest } = props
    return (
      <ChakraCheckbox.Root ref={rootRef} {...rest}>
        <ChakraCheckbox.HiddenInput ref={ref} {...inputProps} />
        <ChakraCheckbox.Control>
          {icon || <ChakraCheckbox.Indicator strokeWidth="4" />}
        </ChakraCheckbox.Control>
        {children != null && (
          <ChakraCheckbox.Label>{children}</ChakraCheckbox.Label>
        )}
      </ChakraCheckbox.Root>
    )
  },
)

export { Checkbox } from './checkbox.tsx'
export type { CheckboxProps } from './checkbox.tsx'
export {
  CheckboxGroup,
  type CheckboxGroupProps,
} from '@chakra-ui/react/checkbox'
