import { createSlotRecipeContext } from '@chakra-ui/react'

export const {
  useStyles: useLoadingOverlayStyles,
  withContext,
  withProvider,
} = createSlotRecipeContext({
  key: 'suiLoadingOverlay',
})

import * as React from 'react'

import { Button, Card, Container, Text } from '@chakra-ui/react'

import { LoadingOverlay } from './index.ts'

export default {
  title: 'Components/LoadingOverlay',
  decorators: [
    (Story: any) => (
      <Container mt="40px">
        <Story />
      </Container>
    ),
  ],
}

export const Basic = () => (
  <Card.Root width="400px" height="200px" overflow="hidden">
    <LoadingOverlay.Root>
      <LoadingOverlay.Spinner />
    </LoadingOverlay.Root>
  </Card.Root>
)

export const Overlay = () => (
  <Card.Root width="400px" height="200px" pos="relative" p="4">
    <Text>This content will be overlayed.</Text>
    <LoadingOverlay.Root variant="overlay">
      <LoadingOverlay.Spinner />
    </LoadingOverlay.Root>
  </Card.Root>
)

export const Fullscreen = () => (
  <LoadingOverlay.Root variant="fullscreen">
    <LoadingOverlay.Spinner />
  </LoadingOverlay.Root>
)

export const WithText = () => (
  <Card.Root width="400px" height="200px">
    <LoadingOverlay.Root borderRadius="md">
      <LoadingOverlay.Text>Loading...</LoadingOverlay.Text>
    </LoadingOverlay.Root>
  </Card.Root>
)

export const ExitAnimation = () => {
  const [loading, setLoading] = React.useState(false)

  React.useEffect(() => {
    if (loading) {
      setTimeout(() => {
        setLoading(false)
      }, 4000)
    }
  }, [loading])

  return (
    <Card.Root width="400px" height="200px" pos="relative" p="4">
      <Button onClick={() => setLoading(true)}>Show loader</Button>
      <LoadingOverlay.Root variant="overlay" loading={loading}>
        <LoadingOverlay.Spinner />
      </LoadingOverlay.Root>
    </Card.Root>
  )
}

'use client'

import React, { forwardRef } from 'react'

import {
  Presence,
  type PresenceBaseProps,
  splitPresenceProps,
} from '@ark-ui/react/presence'
import { HTMLChakraProps, SlotRecipeProps, chakra } from '@chakra-ui/react'

import { Spinner } from '../spinner/index.ts'
import { withContext, withProvider } from './loading-overlay.context.ts'

interface LoadingOverlayProps
  extends HTMLChakraProps<'div'>,
    SlotRecipeProps<'suiLoadingOverlay'>,
    PresenceBaseProps {
  /**
   * Show or hide the LoadingOverlay.
   * @default true
   */
  loading?: boolean
}

const LoadingOverlay = forwardRef<HTMLDivElement, LoadingOverlayProps>(
  (props, ref) => {
    const { children, loading = true, ...rest } = props

    const [presenceProps, rootProps] = splitPresenceProps(rest)

    return (
      <Presence present={loading} {...presenceProps} asChild>
        <chakra.div ref={ref} {...rootProps}>
          {children}
        </chakra.div>
      </Presence>
    )
  },
)

const LoadingOverlayRoot = withProvider<HTMLDivElement, LoadingOverlayProps>(
  LoadingOverlay,
  'root',
)

LoadingOverlayRoot.displayName = 'LoadingOverlay'

const LoadingOverlaySpinner = Spinner

interface LoadingTextProps extends HTMLChakraProps<'p'> {}

const LoadingOverlayText = withContext<HTMLParagraphElement, LoadingTextProps>(
  'p',
  'text',
)

export {
  LoadingOverlayRoot as Root,
  LoadingOverlaySpinner as Spinner,
  LoadingOverlayText as Text,
}
export type { LoadingOverlayProps as RootProps, LoadingTextProps as TextProps }
