import React from 'react'

import { Stack } from '@chakra-ui/react'
import { Meta, StoryObj } from '@storybook/react-vite'

import { SegmentedControl } from './index.ts'

export default {
  title: 'Components/Segmented Control',
  component: SegmentedControl,
} satisfies Meta

type Story = StoryObj<typeof SegmentedControl>

export const Default: Story = {
  args: {
    defaultValue: 'One',
    items: ['One', 'Two', 'Three'],
  },
}

export const Sizes = () => {
  return (
    <Stack alignItems="flex-start">
      <SegmentedControl
        defaultValue="One"
        items={['One', 'Two', 'Three']}
        size="xs"
      />
      <SegmentedControl
        defaultValue="One"
        items={['One', 'Two', 'Three']}
        size="sm"
      />
      <SegmentedControl
        defaultValue="One"
        items={['One', 'Two', 'Three']}
        size="md"
      />
      <SegmentedControl
        defaultValue="One"
        items={['One', 'Two', 'Three']}
        size="lg"
      />
    </Stack>
  )
}

'use client'

import { forwardRef, useMemo } from 'react'

import { For } from '@chakra-ui/react/for'
import { SegmentGroup } from '@chakra-ui/react/segment-group'

interface Item {
  value: string
  label: React.ReactNode
  disabled?: boolean
}

export interface SegmentedControlProps extends SegmentGroup.RootProps {
  items: Array<string | Item>
}

function normalize(items: Array<string | Item>): Item[] {
  return items.map((item) => {
    if (typeof item === 'string') return { value: item, label: item }
    return item
  })
}

export const SegmentedControl = forwardRef<
  HTMLDivElement,
  SegmentedControlProps
>(function SegmentedControl(props, ref) {
  const { items, ...rest } = props
  const data = useMemo(() => normalize(items), [items])

  return (
    <SegmentGroup.Root ref={ref} {...rest}>
      <SegmentGroup.Indicator />
      <For each={data}>
        {(item) => (
          <SegmentGroup.Item
            key={item.value}
            value={item.value}
            disabled={item.disabled}
          >
            <SegmentGroup.ItemText>{item.label}</SegmentGroup.ItemText>
            <SegmentGroup.ItemHiddenInput />
          </SegmentGroup.Item>
        )}
      </For>
    </SegmentGroup.Root>
  )
})
